def paths_to_title(paths):
	temp = []
	for path in paths[:-1]:
		temp.extend(path[1].split(" ")[:-1])
	temp.append(paths[-1][1])
	return " ".join(temp)

def make_adj(titles):
	adj = defaultdict(list)
	for title in titles:
		splitted = title.split(" ")
		if len(splitted) > 1:
			first, last = splitted[0].upper(), splitted[-1].upper()
			adj[first].append((last, title, len(splitted)))
	return adj

def make_longest_title(titles):
	adj = make_adj(titles)

	def get_longest(node, seen=set(), path=[], length=0):
		unseen = [n for n in adj[node] if n not in seen]
		if not unseen: return (length - len(path) + 1, paths_to_title(path))

		best = (0, "")
		for child in adj[node]:
			res = get_longest(child[0], seen | set(child[0]), path + [child], child[2] + length)
			best = max(best, res)
		return best

	best = (0, "")
	for key in list(adj.keys()):
		best = max(best, get_longest(key))
	return best[1]

print('Output (make_longest_title):', make_longest_title(a_few_titles))

"""
However that is really ugly code and hard to read. There's also a lot of duplicate code.

I wonder if iteration would be better... yes.
"""

def remove_last_word(string: str):
	return " ".join(string.split(" ")[:-1]) + " "


def make_longest_title2(titles):
	adj = make_adj(titles)
	queue = list(adj.keys())
	longest = (0, "")
	while queue:
		node = queue.pop()
		if type(node) == str:  # is entry node
			for end, title, length in adj[node]:
				queue.append((end, title, length, {node}))
		else:
			first, title, length, seen = node
			unseen = [n for n in adj[first] if n[0] not in seen]
			if not unseen: longest = max(longest, (length, title))
			for nxt, nxt_title, nxt_length in unseen:
				nxt_title = remove_last_word(title) + nxt_title
				nxt_length += length
				nxt_seen = seen | {nxt}
				queue.append((nxt, nxt_title, nxt_length, nxt_seen))
	return longest[1]

print('Output (make_longest_title2):', make_longest_title2(lots_of_titles))

